<script>
$('ul.tabs').tabs();
$('.slider').slider({full_width: true});
  

  function makeSquare(){
  
    $('.square.box').each(function(){
    
     var squareHeight = $(this).width();		
    
     $(this).height(squareHeight);
  
		 //middle placement
     var boxInfo = $(this).children('.middle').height();   
  	 var s1 = squareHeight - boxInfo; 
  	 var placement = s1 / 2;
  
     //hover box content placement
     var info = $(this).children().children('.info').height();
  	
  	
  
  	 var s2 = squareHeight - info; 
  	 var placement2 = s2 / 2;  
  
   
  	 $(this).children('.box-info').css({'padding-left': '30px', 'padding-right':'30px'});  
  	 $(this).children().children('.info').css({'top': placement2});  

     $(this).children('.middle').css({'top': placement, 'padding-left' : '30px'});
  	 $(this).children('.box-content').height(squareHeight);
  	 $(this).children('.top').css({'top': '30px'});
  
  
                              
    }); 
  
  
    $('.rect.box').each(function(){
    
     var squareHeight = $(this).width() / 2;                        
  
     $(this).height($(this).width() / 2);
  
		 //middle placement
     var boxInfo = $(this).children('.middle').height();   
  	 var s1 = squareHeight - boxInfo; 
  	 var placement = s1 / 2;
  
     //hover box content placement
     var info = $(this).children().children('.info').height();
  	
  	
  
  	 var s2 = squareHeight - info; 
  	 var placement2 = s2 / 2;  
  
   
  	 $(this).children('.box-info').css({'padding-left': '30px', 'padding-right':'30px'});  
  	 $(this).children().children('.info').css({'top': placement2});  

     $(this).children('.middle').css({'top': placement, 'padding-left' : '30px'});
  	 $(this).children('.box-content').height(squareHeight);
  	 $(this).children('.top').css({'top': '30px'});                              
    });   
  
    
  
  };

  
  $(window).resize(function(){                             
  	makeSquare();  
  });  

  	makeSquare();  
  
</script>

